workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.common.*;
import java.rmi.server.*
import com.rameses.util.*;

class DocumentRedflagWarningController {

    @Binding 
    def binding;

    def list;

    @Service("TagabukidSubayRedflagService")
    def svc;

    def htmlText; 

    void init() {
        def sb = new StringBuffer();
        sb.append("THERE ARE UNRESOLVED ISSUES: <br>")
        list.each {
                sb.append( "--------<br>CASE NO: '+ it.caseno +'<br>DIN: ' + it.din + '<br>MESSAGE: ' + it.message + "<br>FILED BY " + it.filedby.name + "<br>--------<br>");
        }
        htmlText = sb.toString();
    }

    def doOk() {
        svc.notifyMessage( list );
        return '_close';
    }

    def doCancel() {
        return "_close";
    }

}
