workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.common.*;
import java.rmi.server.*
import com.rameses.util.*;

class DocumentOfflineReconciliationFlowController extends PageFlowController {

     @Caller
    def caller;

    @Binding 
    def binding;

    @Script("User")
    def user;

    @Service("TagabukidDTSService")
    def service;
    def mode = 'create'
    def handler;
    boolean pass;
    def reconciliationActions = ["outgoing", "receive", "archived"];
    def din;
    def entity = [:];

    void init() {
        mode = 'create'
        entity.din = null;
        entity.title = null;
        entity.author = null;
        entity.description = null;
        entity.documenttype = [name:null];
        entity.recordlog = [datecreated:null,createdbyuser:null];
        entity.state = null;
        entity.refid = null;
        entity.org = null;
    }

    void confirm(){
        mode = 'confirm'
    }
    public def barcodesearch(){
        entity.searchtext = din
        entity.mode = 'offline'
        searchdocument()
    }
    def searchdocument(){
        def doc = service.lookupDIN(entity)
        if (!doc){
            throw new Exception("Invalid DIN");
        }

        if (doc.size == 1){
            addtolist(doc[0])
        }
        else if(doc.size > 1){
            return Inv.lookupOpener('din:lookup',[
                    entity: entity,
                    onselect :{
                        entity = it
                        din = it.din
                        binding.refresh('entity');
                    }
                ])
        }else{
            throw new Exception("Invalid DIN");
        }
    }

    void save(){
        if( mode == 'confirm' ) { 
            service.createOfflineLog( entity );
        }
    }
}
